function startAnimation() {
    if ($('section:not(.inview)').length > 0) {
        $('section:not(.inview)').each(function () {
            var eh = $(this).outerHeight();
            var et = $(this).offset().top;
            if (is_shown(eh, et)) {
                $(this).addClass('inview');
                if ($(this).find('img[temp-src]').length > 0) {
                    $(this).find('img[temp-src]').each(function () {
                        $(this).attr('src', $(this).attr('temp-src'));
                        $(this).removeAttr('temp-src');
                    });
                }
                if ($(this).find('source[temp-src]').length > 0) {
                    $(this).find('source[temp-src]').each(function () {
                        $(this).attr('src', $(this).attr('temp-src'));
                        $(this).removeAttr('temp-src');
                    });
                }
                if ($(this).find('source[temp-srcset]').length > 0) {
                    $(this).find('source[temp-srcset]').each(function () {
                        $(this).attr('srcset', $(this).attr('temp-srcset'));
                        $(this).removeAttr('temp-srcset');
                    });
                }
                if ($(this).find('video[data-poster-webp]').length > 0) {
                    $(this).find('video[data-poster-webp]').each(function () {
                        $(this).attr('poster', $(this).attr('data-poster-webp'));
                        $(this).removeAttr('data-poster-webp');
                    });
                }
                if ($(this).find('video.lazy').length > 0) {
                    $(this).find('video.lazy source').each(function () {
                        $(this).attr('src', $(this).attr('data-src'));
                        $(this).removeAttr('data-src');
                    });
                    $(this).removeClass('lazy');
                }
                if ($(this).find('iframe[temp-src]').length > 0) {
                    $(this).find('iframe[temp-src]').each(function () {
                        $(this).attr('src', $(this).attr('temp-src'));
                        $(this).removeAttr('temp-src');
                    });
                }
            }
        });
    }
    if ($('footer:not(.inview)').length > 0) {
        $('footer:not(.inview)').each(function () {
            var eh = $(this).outerHeight();
            var et = $(this).offset().top;
            if (is_shown(eh, et)) {
                $(this).addClass('inview');
                if ($(this).find('img[temp-src]').length > 0) {
                    $(this).find('img[temp-src]').each(function () {
                        $(this).attr('src', $(this).attr('temp-src'));
                        $(this).removeAttr('temp-src');
                    });
                }
                if ($(this).find('source[temp-src]').length > 0) {
                    $(this).find('source[temp-src]').each(function () {
                        $(this).attr('src', $(this).attr('temp-src'));
                        $(this).removeAttr('temp-src');
                    });
                }
                if ($(this).find('video[data-poster-webp]').length > 0) {
                    $(this).find('video[data-poster-webp]').each(function () {
                        $(this).attr('poster', $(this).attr('data-poster-webp'));
                        $(this).removeAttr('data-poster-webp');
                    });
                }
                if ($(this).find('iframe[temp-src]').length > 0) {
                    $(this).find('iframe[temp-src]').each(function () {
                        $(this).attr('src', $(this).attr('temp-src'));
                        $(this).removeAttr('temp-src');
                    });
                }
            }
        });
    }
    if ($('.mfp-content .new-modal-image:not(.inview)').length > 0) {
        $('.mfp-content .new-modal-image:not(.inview)').each(function () {
            var eh = $(this).outerHeight();
            var et = $(this).offset().top;
            if (is_shown(eh, et)) {
                $(this).addClass('inview');
                if ($(this).find('img[temp-src]').length > 0) {
                    $(this).find('img[temp-src]').each(function () {
                        $(this).attr('src', $(this).attr('temp-src'));
                        $(this).removeAttr('temp-src');
                    });
                }
            }
        });
    }
    if ($('.header__top--banner.show__block:not(.inview)').length > 0) {
        $('.header__top--banner.show__block:not(.inview)').each(function () {
            var eh = $(this).outerHeight();
            var et = $(this).offset().top;
            if (is_shown(eh, et)) {
                $(this).addClass('inview');
                if ($(this).find('img[temp-src]').length > 0) {
                    $(this).find('img[temp-src]').each(function () {
                        $(this).attr('src', $(this).attr('temp-src'));
                        $(this).removeAttr('temp-src');
                    });
                }
            }
        });
    }
    if ($('.header__top--banner--catalog.show__block:not(.inview)').length > 0) {
        $('.header__top--banner--catalog.show__block:not(.inview)').each(function () {
            var eh = $(this).outerHeight();
            var et = $(this).offset().top;
            if (is_shown(eh, et)) {
                $(this).addClass('inview');
                if ($(this).find('img[temp-src]').length > 0) {
                    $(this).find('img[temp-src]').each(function () {
                        $(this).attr('src', $(this).attr('temp-src'));
                        $(this).removeAttr('temp-src');
                    });
                }
            }
        });
    }
    if ($('.cr.sctn').length > 0) {
        var eh = $('.cr.sctn').outerHeight();
        var et = $('.cr.sctn').offset().top;
        if (is_shown(eh, et)) {
            $('.cr-bottom-item-upd h3:not(.finish)').each(function () {
                $(this).addClass('finish');
                if (parseInt($(this).text()) > 1000) {
                    $(this).prop('Counter', 0).animate({
                        Counter: $(this).text()
                    }, {
                        duration: 4000,
                        easing: 'swing',
                        step: function (now) {
                            $(this).text(parseInt(now));
                        }
                    });
                } else {
                    $(this).prop('Counter', 0).animate({
                        Counter: $(this).text()
                    }, {
                        duration: 2000,
                        easing: 'swing',
                        step: function (now) {
                            $(this).text(parseInt(now));
                        }
                    });
                }
            });
        }
    }
}

function is_shown(eh, et) {
    var wt = $(window).scrollTop();
    var wh = $(window).height();

    if (wt + wh >= et && wt + wh - eh * 2 <= et + (wh - eh)) {
        return true;
    } else {
        return false;
    }
}
